import React, {Component} from 'react';

// https://www.googleapis.com/youtube/v3/search?key=AIzaSyB4xNW_TeLDTO9tKxmHQAZskoy9Na0sxR0&channelId=UCE_M8A5yxnLfW0KghEeajjw&part=snippet,id&order=date&maxResults=1

class Youtube extends Component {

	constructor(){
		super();
		this.state = {
			youTubeVideos: []
		}
	}

	componentDidMount(){
		fetch("https://www.googleapis.com/youtube/v3/search?key=AIzaSyB4xNW_TeLDTO9tKxmHQAZskoy9Na0sxR0&channelId=UCE_M8A5yxnLfW0KghEeajjw&part=snippet,id&order=date&maxResults=3")
			.then(response => response.json())
			// .then(data => {
			// 	this.setState({
			// 		youTubeVideos: data
			// 	})
			// })
			// .then(data => {
			// 	console.log(data.items[0]['snippet']['title'])
			// }) 

			.then((data) => {
				const youTubeVideos = data.items.map(vidObject => vidObject);
				this.setState({youTubeVideos});

			}) 

			// .then(data => {
			// 	console.log(data.items);
			// 	const videoId = data.items.map(vidObject => vidObject.id.videoID);
			// 	console.log(videoId);
			// 	this.setState({videoId});
			// 	console.log(this.state.videoId);
			// }) 

			// <div> {this.state.youTubeVideos.map(videos => {items[0]['snippet']['title'])}} </div>
	}
	render () {



		// const vidOb = this.state.youTubeVideos.map(vidObject => vidObject.snippet); 
		// console.log(vidOb);

		return (

			<div className="allVideosWrapper">
				{
						// console.log(this.state.youTubeVideos);
						this.state.youTubeVideos.map((singleVideo, i) => {
							console.log(singleVideo);
							let vidId = singleVideo.id.videoId; 
										console.log(vidId);

							let vidLink = `https://www.youtube.com/watch?v=${vidId}`;
							let videoWrapper =
							<div key={i} className="singleVideoWrapper">
								<div className="videoThumbnail"><a href={vidLink} target="_blank"><img src={singleVideo.snippet.thumbnails.high.url} /></a></div>
								<div className="videoInfoWrapper">
									<div className="videoTitle">{singleVideo.snippet.title}</div>			
									<div className="videoDesc">{singleVideo.snippet.description}</div>
								</div>
							</div>;
							return videoWrapper; 
						})
				}

				{this.videoWrapper}

			</div>
		);
	}
}

export default Youtube;
